export function USMap({ onStateClick }: USMapProps) {
  const navigate = useNavigate();
  const [hoveredState, setHoveredState] = useState<string | null>(null);
  const { data: stateScores, isLoading } = useStateScores();

  const handleStateClick = (abbr: string) => {
    if (onStateClick) {
      onStateClick(abbr);
    } else {
      navigate(`/state/${abbr}`);
    }
  };

  const hoveredData = hoveredState 
    ? stateScores?.find(s => s.abbr === hoveredState) 
    : null;

  if (isLoading) {
    return (
      <div className="relative w-full aspect-[959/593] flex items-center justify-center">
        <Skeleton className="w-full h-full rounded-lg" />
      </div>
    );
  }

  return (
    <div className="relative w-full">
      {/* Tooltip - positioned outside SVG */}
      {hoveredData && (
        <div className="absolute top-4 left-4 z-20 rounded-lg bg-card border border-border shadow-civic-lg p-4 min-w-[180px] animate-scale-in pointer-events-none">
          <h4 className="font-serif font-semibold text-foreground">{hoveredData.name}</h4>
          <div className="flex items-center justify-between mt-2">
            <span className="text-sm text-muted-foreground">Avg. Score</span>
            <span 
              className="text-lg font-bold"
              style={{ color: getScoreColor(hoveredData.score) }}
            >
              {hoveredData.score}
            </span>
          </div>
          <div className="flex items-center justify-between mt-1">
            <span className="text-xs text-muted-foreground">Members</span>
            <span className="text-sm font-medium text-foreground">{hoveredData.memberCount}</span>
          </div>
        </div>
      )}

      {/* SVG Map */}
      <svg
        viewBox="0 0 959 593"
        className="w-full h-auto"
        preserveAspectRatio="xMidYMid meet"
      >
        <defs>
          <linearGradient id="mapBg" x1="0%" y1="0%" x2="100%" y2="100%">
            <stop offset="0%" stopColor="hsl(var(--background))" />
            <stop offset="100%" stopColor="hsl(var(--muted))" />
          </linearGradient>

          <filter id="stateShadow" x="-5%" y="-5%" width="110%" height="110%">
            <feDropShadow dx="0" dy="1" stdDeviation="1" floodOpacity="0.1" />
          </filter>
          <filter id="stateHover" x="-10%" y="-10%" width="120%" height="120%">
            <feDropShadow dx="0" dy="2" stdDeviation="3" floodOpacity="0.25" />
          </filter>
        </defs>

        <rect
          x="0"
          y="0"
          width="959"
          height="593"
          fill="url(#mapBg)"
          rx="12"
          ry="12"
          style={{ pointerEvents: "none" }}
        />

        {/* State Paths */}
        {Object.entries(statePaths).map(([abbr, pathD]) => {
          const stateData = stateScores?.find(s => s.abbr === abbr);
          const score = stateData?.score ?? 50;
          const isHovered = hoveredState === abbr;

          return (
            <path
              key={abbr}
              d={pathD}
              fill={getScoreColor(score)}
              stroke="hsl(var(--background))"
              strokeWidth="1.5"
              className="cursor-pointer transition-opacity duration-200"
              style={{
                filter: isHovered ? "url(#stateHover)" : "url(#stateShadow)",
                opacity: isHovered ? 1 : 0.95,
              }}
              onClick={() => handleStateClick(abbr)}
              onMouseEnter={() => setHoveredState(abbr)}
              onMouseLeave={() => setHoveredState(null)}
            />
          );
        })}
      </svg>

      {/* Legend - positioned below SVG */}
      <div className="flex justify-center gap-6 mt-6 flex-wrap">
        {[
          { color: "hsl(var(--score-excellent))", label: "80+ Excellent" },
          { color: "hsl(var(--score-good))", label: "70–79 Good" },
          { color: "hsl(var(--score-average))", label: "60–69 Average" },
          { color: "hsl(var(--score-poor))", label: "50–59 Poor" },
          { color: "hsl(var(--score-bad))", label: "<50 Needs Work" },
        ].map(({ color, label }) => (
          <div key={label} className="flex items-center gap-2 text-sm">
            <div className="w-4 h-4 rounded" style={{ backgroundColor: color }} />
            <span className="text-muted-foreground">{label}</span>
          </div>
        ))}
      </div>
    </div>
  );
}
